aws dynamodb create-table --table-name HealthData --attribute-definitions AttributeName=Type,AttributeType=S AttributeName=Date,AttributeType=S --key-schema AttributeName=Date,KeyType=HASH AttributeName=Type,KeyType=RANGE --provisioned-throughput ReadCapacityUnits=2,WriteCapacityUnits=2 --table-class STANDARD --endpoint-url http://localhost:8000
aws dynamodb create-table --table-name tokens --attribute-definitions AttributeName=TokenType,AttributeType=S --key-schema AttributeName=TokenType,KeyType=HASH --provisioned-throughput ReadCapacityUnits=1,WriteCapacityUnits=1 --table-class STANDARD --endpoint-url http://localhost:8000